import React, { useState, useEffect } from "react";
import navigationContext from "../../contexts/navigationsContext";
import navigations from "../../Data/navigation.json";
import posts from "../../Data/posts.json";
import useToggle from "../../Hooks/useToggle";
import BackToTop from "../Helpers/BackToTop";
import Drawer from "../Mobile/Drawer";
import Footer from "../Partials/Footers/Home/index";
import Header from "../Partials/Headers/Header";

import DarkEditorPackSection from "./DarkEditorPackSection";

import EditorPackSection from "./EditorPackSection";
import HeroSliderSection from "./HeroSliderSection";
import PopularNewsSection from "./PopularNewsSection";

function Home() {
  const navigationData = navigations.navigations;
  // const editorPackSectionDatas = posts.posts;
  const [technologyNewsData, setTechnologyNewsData] = useState([]);

  const [editorPackSectionDatar, setEditorPackSectionDatas] = useState([]);
  const [editorPackSectionDatarOne, setEditorPackSectionDatasOne] = useState([]);

  const [editorPackSectionDatas, setDarkEditorPackSectionData] = useState([]);  /*state to hold the data received from the API.*/
  const [drawer, setDrawer] = useToggle(false);

  useEffect(() => {
    const fetchData = async () => {
      try {

        const response = await fetch('http://127.0.0.1:5000/api/v1/articles/');
        
        if (!response.ok) {
          throw new Error('Mariam, Failed to fetch data');
        }

        const data = await response.json();
        const technologyPosts = data[2];

        const darkEditorPackSectionData = data[1];

        console.log("1");

        const EditorPackSectionData = data[3];
        const EditorPackSectionDataOne = data[5];

        console.log(data);
        console.log(EditorPackSectionDataOne);
        console.log(EditorPackSectionData);

        setEditorPackSectionDatas(EditorPackSectionData);

        setEditorPackSectionDatasOne(EditorPackSectionDataOne);

        setDarkEditorPackSectionData(darkEditorPackSectionData);

        setTechnologyNewsData(technologyPosts); // Assuming you want to use the same data for technology news

      } catch (error) {
        console.error('Error fetching data:', error.message);
        // Handle error state, show error message to user, etc.
      }
    };

    fetchData();
  }, []);

  return (
    <>
      <Drawer
        drawer={drawer}
        action={setDrawer.toggle}
        navigationData={navigationData}
      />
      <navigationContext.Provider value={{ navigationData }}>
        <Header drawerAction={setDrawer.toggle} />
      </navigationContext.Provider>

      <EditorPackSection editorPackSectionDatas={editorPackSectionDatar} editorPackSectionDatasOne={editorPackSectionDatarOne} />

      <DarkEditorPackSection editorPackSectionDatas={editorPackSectionDatas} />

      {/* <PopularNewsSection popularNewsData={popularNewsData} /> */}

      <Footer />
      <BackToTop />
    </>
  );

}

export default Home;












