import React, { useState, useEffect } from "react";
import navigationContext from "../../contexts/navigationsContext";
import navigations from "../../Data/navigation.json";
import posts from "../../Data/posts.json";
import useToggle from "../../Hooks/useToggle";
import BackToTop from "../Helpers/BackToTop";
import Drawer from "../Mobile/Drawer";
import Footer from "../Partials/Footers/Home/index";
import Header from "../Partials/Headers/Header";

import DarkEditorPackSection from "./DarkEditorPackSection";
import EditorPackSection from "./EditorPackSection";
import HeroSliderSection from "./HeroSliderSection";
import PopularNewsSection from "./PopularNewsSection";

function Home() {
  const navigationData = navigations.navigations;

  // State to hold the fetched data for EditorPackSection components
  const [editorPackSectionData, setEditorPackSectionData] = useState([]);
  const [editorPackSectionDataOne, setEditorPackSectionDataOne] = useState([]);

  // State to hold the fetched data for DarkEditorPackSection component
  const [darkEditorPackSectionData, setDarkEditorPackSectionData] = useState([]);

  // State for toggling the drawer (mobile menu)
  const [drawer, setDrawer] = useToggle(false);

  // Fetch data from API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/v1/articles/');
        
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }

        const data = await response.json();

        // Extract data based on assumed array indices
        const technologyPosts = data[2];
        const darkEditorPackSection = data[1];
        const editorPackSection = data[3];
        const editorPackSectionOne = data[5];

        // Debugging: Check the structure of the fetched data
        console.log(data);
        console.log(editorPackSectionOne);
        console.log(editorPackSection);

        // Update the state with the fetched data
        setEditorPackSectionData(editorPackSection);
        setEditorPackSectionDataOne(editorPackSectionOne);
        setDarkEditorPackSectionData(darkEditorPackSection);

      } catch (error) {
        console.error('Error fetching data:', error.message);
      }
    };

    fetchData();
  }, []);

  return (
    <>
      <Drawer
        drawer={drawer}
        action={setDrawer.toggle}
        navigationData={navigationData}
      />
      <navigationContext.Provider value={{ navigationData }}>
        <Header drawerAction={setDrawer.toggle} />
      </navigationContext.Provider>

      {/* Pass fetched data to EditorPackSection components */}
      <EditorPackSection 
        editorPackSectionDatas={editorPackSectionData} 
        editorPackSectionDatasOne={editorPackSectionDataOne} 
      />

      {/* Pass fetched data to DarkEditorPackSection component */}
      <DarkEditorPackSection 
        editorPackSectionDatas={darkEditorPackSectionData} 
      />

      <Footer />
      <BackToTop />
    </>
  );
}

export default Home;




