import React from "react";
import PostCardStyleHome from "./Cards/PostCardStyleHome";

function NewsGrid({
  editorPackSectionDatas = {}, // Default empty object to prevent undefined errors
  editorPackSectionDatasOne = {}, // Default empty object to prevent undefined errors
  dataLength = 2,
  gridClasses = "col-lg-3 col-md-6",
}) {
  // Combine data into an array for easier iteration and dynamic rendering
  const dataList = [editorPackSectionDatas, editorPackSectionDatasOne];

  return (
    <div className="row g-0">
      {dataList.slice(0, dataLength).map((data, index) => {
        // Check if data is undefined or empty
        if (!data || Object.keys(data).length === 0) {
          return null; // Skip rendering if data is invalid
        }

        return (
          <div key={data.category_id || index} className={gridClasses}>
            <PostCardStyleHome
              datas={{
                image: data.image || '',       // Default empty string if image is undefined
                category: data.category || '', // Default empty string if category is undefined
                date: data.date || '',         // Default empty string if date is undefined
                title: data.title || '',       // Default empty string if title is undefined
                user: data.user || '',         // Default empty string if user is undefined
              }}
            />
          </div>
        );
      })}
    </div>
  );
}

export default NewsGrid;





