import "react-quill/dist/quill.snow.css";
import "./new.scss";
import Sidebar from '../../../components/admin/sidebar/Sidebar';
import Navbar from '../../../components/admin/navbar/Navbar';
import DriveFolderUploadOutlinedIcon from "@mui/icons-material/DriveFolderUploadOutlined";
import React, { useState } from "react";
import ReactQuill from "react-quill";

const New = () => {
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("technology"); // Default category
  const [successMessage, setSuccessMessage] = useState(""); // Success message state

  const categories = ["Politics", "Sports", "Entertainment"]; // Example categories

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const handleTitleChange = (e) => {
    const titleValue = e.target.value;
    setTitle(titleValue);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Upload file first
    const formData = new FormData();
    formData.append("file", file);

    try {
      const uploadResponse = await fetch('http://127.0.0.1:5000/api/v1/articles/register', {
        method: 'POST',
        body: formData,
      });

      if (!uploadResponse.ok) {
        throw new Error('Failed to upload file');
      }

      const { imagePath } = await uploadResponse.json();
      console.log('File uploaded successfully:', imagePath);

      // Create content data to be sent to /content endpoint
      const contentData = {
        name: title,
        description: description,
        image_path: imagePath,
        slug: title.toLowerCase().replace(/\s+/g, '-'), // Generate slug from title
        category: category,
        user_id: 1, // Replace with actual user_id
      };

      const createResponse = await fetch('http://127.0.0.1:5000/api/v1/articles/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(contentData),
      });

      if (!createResponse.ok) {
        throw new Error('Failed to add content');
      }

      const data = await createResponse.json();
      console.log('Content created successfully:', data);
      
      // Show success message and reset form fields
      setSuccessMessage("Article saved successfully!");
      setFile(null);
      setTitle("");
      setDescription("");
      setCategory("technology"); // Reset to default category

      // Hide success message after 3 seconds
      setTimeout(() => {
        setSuccessMessage("");
      }, 3000);
      
    } catch (error) {
      console.error('Error adding content:', error.message);
      // Handle error state, show error message to user, etc.
    }
  };

  return (
    <div className="newArticle">
      <Sidebar />
      <div className="newContainer">
        <Navbar />

        <div className="top">
          <h1>Add New Article</h1>
        </div>

        <div className="bottom">

          <div className="left">
            <img
              src={file ? URL.createObjectURL(file) : "https://icon-library.com/images/no-image-icon/no-image-icon-0.jpg"}
              alt=""
            />
          </div>

          <div className="right">
            <form onSubmit={handleSubmit}>
              
              <div className="formInput">
                <label htmlFor="file">Image: <DriveFolderUploadOutlinedIcon className="icon" /></label>
                <input type="file" id="file" onChange={handleFileChange} style={{ display: "none" }}/>
              </div>
              <div className="formInput">
                <label>Title</label>
                <input type="text" placeholder="Enter Title" value={title} onChange={handleTitleChange} /> {/* Handle title change*/}
              </div>
              <div className="formInput">
                <label>Description</label>
                <ReactQuill value={description} onChange={setDescription} />
              </div>
              <div className="formInput">
                <label>Category</label>
                <select value={category} onChange={handleCategoryChange}>
                  {categories.map((cat) => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </select>
              </div>
              <button type="submit" className="submit-button">Send</button>
            </form>
            {successMessage && <p className="success-message">{successMessage}</p>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default New;
