import React, { useState, useEffect } from "react";
import navigationContext from "../../contexts/navigationsContext";
import navigations from "../../Data/navigation.json";
import useToggle from "../../Hooks/useToggle";
import BackToTop from "../Helpers/BackToTop";
import Drawer from "../Mobile/Drawer";
import Footer from "../Partials/Footers/Home/index";
import Header from "../Partials/Headers/Header";
import DarkEditorPackSection from "./DarkEditorPackSection";
import EditorPackSection from "./EditorPackSection";

function Home() {
  const navigationData = navigations.navigations;
  const [editorPackSectionDatas, setEditorPackSectionDatas] = useState([]);
  const [drawer, setDrawer] = useToggle(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/api/v1/articles/");
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        const data = await response.json();

        // Check the structure of the data
        console.log("Fetched data:", data);

        if (Array.isArray(data)) {
          setEditorPackSectionDatas(data);
        } else {
          throw new Error("Unexpected data structure");
        }
      } catch (error) {
        console.error("Error fetching data:", error.message);
      }
    };

    fetchData();
  }, []);

  return (
    <>
      <Drawer drawer={drawer} action={setDrawer.toggle} navigationData={navigationData} />
      <navigationContext.Provider value={{ navigationData }}>
        <Header drawerAction={setDrawer.toggle} />
      </navigationContext.Provider>

      <EditorPackSection editorPackSectionDatas={editorPackSectionDatas.filter(article => article.category === "Politics")} />
      <DarkEditorPackSection editorPackSectionDatas={editorPackSectionDatas.filter(article => article.category === "Technology")} />

      <Footer />
      <BackToTop />
    </>
  );
}

export default Home;











