import React, { useState, useEffect } from "react";
import navigationContext from "../../contexts/navigationsContext";
import navigations from "../../Data/navigation.json";
import useToggle from "../../Hooks/useToggle";
import BackToTop from "../Helpers/BackToTop";
import Drawer from "../Mobile/Drawer";
import Footer from "../Partials/Footers/Home/index";
import Header from "../Partials/Headers/Header";

import DarkEditorPackSection from "./DarkEditorPackSection";
import EditorPackSection from "./EditorPackSection";

function Home() {
  const navigationData = navigations.navigations;
  const [technologyArticles, setTechnologyArticles] = useState([]);
  const [otherArticles, setOtherArticles] = useState([]);
  const [drawer, setDrawer] = useToggle(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/v1/articles/');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();

        // Filter articles based on category
        const techArticles = data.filter(article => article.category === 'Technology');
        const nonTechArticles = data.filter(article => article.category !== 'Technology');

        // Update state with the filtered articles
        setTechnologyArticles(techArticles);
        setOtherArticles(nonTechArticles);

      } catch (error) {
        console.error('Error fetching data:', error.message);
      }
    };

    fetchData();
  }, []);

  return (
    <>
      <Drawer
        drawer={drawer}
        action={setDrawer.toggle}
        navigationData={navigationData}
      />
      <navigationContext.Provider value={{ navigationData }}>
        <Header drawerAction={setDrawer.toggle} />
      </navigationContext.Provider>

      {/* Render EditorPackSection with non-technology articles */}
      <EditorPackSection editorPackSectionDatas={otherArticles} />

      {/* Render DarkEditorPackSection with technology articles */}
      <DarkEditorPackSection editorPackSectionDatas={technologyArticles} />

      <Footer />
      <BackToTop />
    </>
  );
}

export default Home;




