import "./new.scss";
import Sidebar from '../../../components/admin/sidebar/Sidebar';
import Navbar from '../../../components/admin/navbar/Navbar';
import DriveFolderUploadOutlinedIcon from "@mui/icons-material/DriveFolderUploadOutlined";
import { useState, useEffect } from "react";

const New = ({ inputs=[], title }) => {
  const [file, setFile] = useState("");

  useEffect(() => {
    return () => {
      if (file) {
        useEffect(() => {
    return () => {
      if (file) {
        URL.revokeObjectURL(file); // Clean up the URL to avoid memory leaks
      }
    };
  }, [file]);; // Clean up the URL to avoid memory leaks
      }
    };
  }, [file]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Input validation
    const email = e.target[3].value;
    const password = e.target[5].value;
    const confirmPassword = e.target[6].value;

    if (password !== confirmPassword) {
      console.error("Passwords do not match");
      return;
    }

    if (!email.includes('@')) {
      console.error("Invalid email");
      return;
    }

    const formData = new FormData();
    formData.append('firstName', e.target[1].value);
    formData.append('lastName', e.target[2].value);
    formData.append('email', e.target[3].value);
    formData.append('contact', e.target[4].value);
    formData.append('password', e.target[5].value);
    formData.append('confirmPassword', e.target[6].value);
    formData.append('phoneNumber', e.target[7].value);
    if (file) {
      formData.append('image', file);
    }

    // Example API request to submit form data
    fetch('http://127.0.0.1:5000/api/v1/auth/users', {
      method: 'POST',
      body: formData,
    })
      .then(response => response.json())
      .then(data => {
        console.log("User created:", data);
      })
      .catch(error => console.error("Error creating user:", error));
  };

  return (
    <div className="new">
      <Sidebar />
      <div className="newContainer">
        <Navbar />
        <div className="top">
          <h1>Add New User</h1>
        </div>
        <div className="bottom">
          <div className="left">
            <img
              src={
                file
                  ? URL.createObjectURL(file)
                  : "https://icon-library.com/images/no-image-icon/no-image-icon-0.jpg"
              }
              alt=""
            />
          </div>
          <div className="right">
            <form onSubmit={handleSubmit}>
              <div className="formInput">
                <label htmlFor="file">
                  Image: <DriveFolderUploadOutlinedIcon className="icon" />
                </label>
                <input
                  type="file"
                  id="file"
                  onChange={(e) => setFile(e.target.files[0])}
                  style={{ display: "none" }}
                />
              </div>
              <div className="formInput">
                <label>First Name</label>
                <input type="text" placeholder="First Name" />
              </div>
              <div className="formInput">
                <label>Last Name</label>
                <input type="text" placeholder="Last Name" />
              </div>
              <div className="formInput">
                <label>Email</label>
                <input type="email" placeholder="email@domain.com" />
              </div>
              <div className="formInput">
                <label>Contact</label>
                <input type="number" placeholder="Contact Number" />
              </div>
              <div className="formInput">
                <label>Password</label>
                <input type="password" placeholder="••••••••••" />
              </div>
              <div className="formInput">
                <label>Confirm Password</label>
                <input type="password" placeholder="••••••••••" />
              </div>
              <div className="formInput">
                <label>Phone Number</label>
                <input type="number" placeholder="Phone Number" />
              </div>
              <button type="submit">Send</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default New;






