import React from "react";
import PropTypes from "prop-types";
import PostCardStyleHome from "./Cards/PostCardStyleHome";

function NewsGrid({
  editorPackSectionDatas,
  dataLength = 4,
  gridClasses = "col-lg-3 col-md-6",
}) {
  return (
    <div className="row g-0">
      {editorPackSectionDatas &&
        editorPackSectionDatas.slice(0, dataLength).map((editorItem, index) => (
          <div key={editorItem.id} className={gridClasses}>
            <PostCardStyleHome
              datas={{
                image: editorItem?.post_images?.editor_pack[index] || "defaultImagePath.jpg",
                category: editorItem?.post_cat?.[0] || "Uncategorized",
                date: editorItem?.post_date || "Unknown date",
                title: editorItem?.post_title || "Untitled",
              }}
            />
          </div>
        ))}
    </div>
  );
}

NewsGrid.propTypes = {
  editorPackSectionDatas: PropTypes.array.isRequired,
  dataLength: PropTypes.number,
  gridClasses: PropTypes.string,
};

export default NewsGrid;







