import "./new.scss";
import Sidebar from '../../../components/admin/sidebar/Sidebar';
import Navbar from '../../../components/admin/navbar/Navbar';
import DriveFolderUploadOutlinedIcon from "@mui/icons-material/DriveFolderUploadOutlined";
import { useState } from "react";

const New = () => {
  const [firstname, setFristname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [usertype, setUsertype] = useState("");
  const [contact, setContact] = useState(""); // Contact state
  const [image, setImage] = useState(null); // Image state
  const [biography, setBiography] = useState(""); // Biography state
  const [successMessage, setSuccessMessage] = useState(""); // Success message state

  const handleFristnameChange = (e) => setFristname(e.target.value);
  const handleLastnameChange = (e) => setLastname(e.target.value);
  const handleEmailChange = (e) => setEmail(e.target.value);
  const handlePasswordChange = (e) => setPassword(e.target.value);
  const handleUsertypeChange = (e) => setUsertype(e.target.value);
  const handleContactChange = (e) => setContact(e.target.value);
  const handleBiographyChange = (e) => setBiography(e.target.value);
  const handleImageChange = (e) => setImage(e.target.files[0]); // Handle file upload

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append("first_name", firstname);
      formData.append("last_name", lastname);
      formData.append("email", email);
      formData.append("contact", contact);
      formData.append("user_type", usertype);
      formData.append("password", password);
      formData.append("biography", biography);
      if (image) formData.append("image", image); // Append image only if available

      const createResponse = await fetch('http://127.0.0.1:5000/api/v1/auth/register', {
        method: 'POST',
        body: formData,
      });

      if (!createResponse.ok) {
        throw new Error('Failed to add user');
      }

      const data = await createResponse.json();
      console.log('User created successfully:', data);

      // Show success message and reset form fields
      setSuccessMessage("User created successfully!");
      setFristname("");
      setLastname("");
      setEmail("");
      setPassword("");
      setUsertype("");
      setContact("");
      setBiography("");
      setImage(null);

      // Hide success message after 3 seconds
      setTimeout(() => setSuccessMessage(""), 3000);

    } catch (error) {
      console.error('Error adding user:', error.message);
    }
  };

  return (
    <div className="newUser">
      <Sidebar />
      <div className="newContainer">
        <Navbar />

        <div className="top">
          <h1>Add New User</h1>
        </div>

        <div className="bottom">
          <div className="right">
            <form onSubmit={handleSubmit}>

              <div className="formInput">
                <label>Enter First Name</label>
                <input type="text" value={firstname} onChange={handleFristnameChange} />
              </div>

              <div className="formInput">
                <label>Enter Last Name</label>
                <input type="text" value={lastname} onChange={handleLastnameChange} />
              </div>

              <div className="formInput">
                <label>Enter Email</label>
                <input type="text" value={email} onChange={handleEmailChange} />
              </div>

              <div className="formInput">
                <label>Enter Password</label>
                <input type="password" value={password} onChange={handlePasswordChange} />
              </div>

              <div className="formInput">
                <label>Enter Contact</label>
                <input type="text" value={contact} onChange={handleContactChange} />
              </div>

              <div className="formInput">
                <label>Enter User Type</label>
                <input type="text" value={usertype} onChange={handleUsertypeChange} />
              </div>

              <div className="formInput">
                <label>Enter Biography</label>
                <textarea value={biography} onChange={handleBiographyChange} />
              </div>

              <div className="formInput">
                <label>Upload Image <DriveFolderUploadOutlinedIcon /></label>
                <input type="file" onChange={handleImageChange} />
              </div>

              <button type="submit" className="submit-button">ADD</button>

            </form>
            {successMessage && <p className="success-message">{successMessage}</p>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default New;






