import React from "react";
import PostCardStyleHome from "./Cards/PostCardStyleHome";

function NewsGrid({
  technologyNewsData,
  dataLength = "3",
  gridClasses = "col-lg-3 col-md-6",
}) {
  return (
    <div className="row g-0">
      {technologyNewsData &&
      technologyNewsData.length > 0 &&
      technologyNewsData.slice(0, dataLength).map((item) => (
      <div key={item.category} className={gridClasses}>
        <PostCardStyleHome
          datas={{
            image: item.image,
            category: item.category,
            date: item.date,
            title: item.title,
            user: item.user,
          }}
        />
      </div>
      ))};
    </div>
  );
}

export default NewsGrid;


