import React from "react";
import PostCardStyleHome from "./Cards/PostCardStyleHome";

function NewsGrid({
  editorPackSectionDatas,
  editorPackSectionDatasOne,
  dataLength = 2,
  gridClasses = "col-lg-3 col-md-6",
}) {
  // Combine data into an array for easier iteration and dynamic rendering
  const dataList = [editorPackSectionDatas, editorPackSectionDatasOne];

  return (
    <div className="row g-0">
      {dataList.slice(0, dataLength).map((data, index) => (
        <div key={data.category_id || index} className={gridClasses}>
          <PostCardStyleHome
            datas={{
              image: data.image,              // Use the image from the data
              category: data.category,        // Use the category from the data
              date: data.date,                // Use the date from the data
              title: data.title,              // Use the title from the data
              user: data.user,                // Use the user from the data
            }}
          />
        </div>
      ))}
    </div>
  );
}

export default NewsGrid;





